import React, { useEffect, useState } from 'react';
import MovieList from '../components/MovieList';
import Navbar from '../components/Navbar';
import axios from 'axios';
import '../App.css';

const HomePage = () => {
    const [nowPlayingMovies, setNowPlayingMovies] = useState([]);
  const [comingSoonMovies, setComingSoonMovies] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');

    // Fetch movie data from Flask API
    useEffect(() => {
        axios.get('http://127.0.0.1:5000/api/movies')
            .then(response => {
                console.log(response.data); // Log the response data for debugging
                setMovies(response.data);
            })
            .catch(error => {
                console.error("Error fetching movies:", error);
            });
    }, []);

    const handleSearch = (event) => {
        setSearchTerm(event.target.value);
    };

    return (
        <div className="App">
            <div style={{ padding: '20px' }}>
                <input
                    type="text"
                    placeholder="Search for a movie..."
                    value={searchTerm}
                    onChange={handleSearch}
                />
            </div>
            <div className="movie-section">
                <h2 style={{ textAlign: 'left' }}>Now Playing</h2>
                <MovieList 
                    movies={movies.filter(movie => movie.title && movie.title.toLowerCase().includes(searchTerm.toLowerCase()))} 
                />
            </div>

            <div className="movie-section">
                <h2 style={{ textAlign: 'left' }}>Coming Soon</h2>
                <MovieList 
                    movies={movies.filter(movie => movie.title && movie.title.toLowerCase().includes(searchTerm.toLowerCase()))} 
                />
            </div>
            <button className="btn red">All Movies</button>
        </div>
    );
};

export default HomePage;
