const MovieList = () => {
    return ( 
        <div className="movie-slider">     
        <div className="movie-list">  
            {Array.from({ length: 10 }).map((_, i) => (
                <MovieCard 
                    key={i} 
                    movie={{ 
                        name: "Movie " + i, 
                        description: "Description " + i, 
                        show_time: "12:00",
                        rating: "PG"
                    }} 
                />
            ))}
        </div>
        <div className="dots">
        {Array.from({ length: 5 }).map((_, i) => (
            <span key={i} className="dot" onClick={() => handleDotClick(i)}></span>
        ))}
    </div>
    </div>
    );
};

export default MovieList;
