import React, { createContext, useState, useEffect } from 'react';
import axios from 'axios';

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [loggedIn, setLoggedIn] = useState(false);
    const [loading, setLoading] = useState(true); // Track loading state

    const checkSession = async () => {
        try {
            const response = await axios.get('http://127.0.0.1:5000/api/check-session', { withCredentials: true });
            setLoggedIn(response.data.logged_in);
        } catch (error) {
            console.error("Error checking session:", error);
            setLoggedIn(false); // Set to false if the check fails
        } finally {
            setLoading(false); // Ensure loading ends after check
        }
    };

    useEffect(() => {
        checkSession();
    }, []);

    if (loading) {
        // Optionally, you could return a loading spinner or null while the session check is in progress
        return <div>Loading...</div>; 
    }

    return (
        <AuthContext.Provider value={{ loggedIn, setLoggedIn }}>
            {children}
        </AuthContext.Provider>
    );
};
