// src/pages/LoginPage.js

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './LoginPage.css'; 
import popcorn from '../images/popcorn.jpg'; 
import Navbar from '../components/Navbar';
import axios from 'axios';  // For API requests

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleLogin = async () => {
    try {
      const response = await axios.post('http://127.0.0.1:5000/api/login', { email, password });
      if (response.data.message === 'Login successful.') {
        navigate('/'); // Redirect to homepage after successful login
      }
    } catch (error) {
      setError('Invalid email or password');
    }
  };

  return (
    <div>
      <Navbar />
      <div className="login-container">
        {/* <Header /> This line adds the Header component here */}
        <div className="login-left">
          <img
            src={popcorn}
            alt="Theater"
            className="theater-img"
          />
        </div>
        <div className="login-right">
          <div className="login-box">
            <h2 className="login-title">Log In</h2>
            <input
              type="text"
              placeholder="Email"
              className="input-field"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            <input
              type="password"
              placeholder="Password"
              className="input-field"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <button className="login-btn" onClick={handleLogin}>Log In</button>
            {error && <p className="error-message">{error}</p>}
            <div className="login-footer">
              <p>
                Don't have an account?{' '}
                <button className="link-button" onClick={() => alert('Create Account clicked')}>
                  Create Account
                </button>
              </p>
              <button className="link-button" onClick={() => alert('Forgot Password clicked')}>
                Forgot Password?
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
