import React, { useContext } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { AuthContext } from '../AuthContext'; // Import your AuthContext
import './Navbar.css';

const Navbar = () => {
    const { loggedIn, setLoggedIn } = useContext(AuthContext); // Use context to get loggedIn state
    const navigate = useNavigate();

    const handleLogout = async () => {
        try {
            await axios.post('http://127.0.0.1:5000/api/logout');
            setLoggedIn(false); // Update the context state
            navigate('/login'); // Redirect to login page
        } catch (error) {
            console.error("Error during logout:", error);
        }
    };

    return (
        <nav className="navbar">
            <div className="logo">
                <h1>Cinema Movies</h1>
            </div>
            {loggedIn ? (
                <button className="btn black" onClick={handleLogout}>Logout</button>
            ) : (
                <Link to="/login">
                    <button className="btn black">Login</button>
                </Link>
            )}
        </nav>
    );
};

export default Navbar;
